import React from "react";

const PersonalInfo = ({space_between_elements, offWhite}) => {

  const info_container = {
    marginTop: `${space_between_elements}`,
    textAlign: "center",
  }

  const about = {
    color: "#828282",
    fontSize: "11px",
    fontWeight: "600",
    marginTop: "7px",
  }

  const name = {
    color: `${offWhite}`,
    textAlign: "center",
    fontSize: "22px",
    fontWeight: "600",
    letterSpacing: "0.075em",
    marginTop: "5px",
  }

  return (
    <div style={info_container}>
      <div style={about}>Paris · Berlin</div>
      <div style={name}>Alexander{'\n'}Devyatchenko Soldak</div>
      <div style={about}>Film Director · Creative Producer</div>
    </div>
  )
}

export default PersonalInfo;
